<?php// Класс контроллераinclude_once('Controller.php');//// Базовый контроллер сайта.//abstract class C_Base extends Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы    protected $login;    protected $logout;	//	// Конструктор.	//	function __construct()	{        $this->title = 'Задачник';        $this->login = 'Вход';        $this->logout = '';        $this->content = '';        if(isset($_SESSION['user'])){            $this->login = '';            $this->logout = 'Выход';        }	}		//	// Генерация базового шаблонаы	//		public function render()	{		$vars = array(		    'title' => $this->title,            'login' => $this->login,            'logout' => $this->logout,            'content' => $this->content,        );		$page = $this->Template('views/v_main.php', $vars);		echo $page;	}	}