<?php// Класс контроллераinclude_once('Controller.php');//// Базовый контроллер сайта.//abstract class C_Base extends Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	//	// Конструктор.	//	function __construct()	{				$this->title = 'Задачник';		$this->logIn = 'Вход для администратора';		$this->content = '';	}		//	// Генерация базового шаблонаы	//		public function render()	{		$vars = array(		    'title' => $this->title,            'logIn' => $this->logIn,            'content' => $this->content,        );		$page = $this->Template('views/v_main.php', $vars);		echo $page;	}	}