<?phprequire_once('db.php');/** * Модель таблицы tasks */// получаем массив задач с условиями пагинации и сортировкиfunction getTasks($results_per_page, $order, $order_value){    //первая задача на странице    $first_result = 0;    if (isset($_GET['first_result'])){        $first_result = $_GET['first_result'];    }    // задаём параметр сортировки    $sort_sql = 'tasks.id';        if ($order_value == 'user_name') {            $sort_sql = 'users.`name`';        } else if ($order_value == 'email') {            $sort_sql = 'tasks.email';        } else if ($order_value == 'status') {            $sort_sql = 'status_name';        }    try {        $q = "SELECT tasks.id, users.`name`, tasks.email, description, id_status, status_name              FROM tasks            LEFT JOIN users ON tasks.id_user = users.id			LEFT JOIN `status` ON tasks.id_status = `status`.id              ORDER BY " . $sort_sql . " " . $order . "            LIMIT " . $first_result . "," . $results_per_page;;        $sql = SQL::getInstance()->Select($q);    } catch (PDOException $e) {        die("Error: " . $e->getMessage());    }    return $sql;}// получаем данные задачи по idfunction getTask($id_task){    $id_task = (int)$id_task;    try {        $q = "SELECT * FROM tasks WHERE id = ".$id_task;        $sql = SQL::getInstance()->Select($q);    }    catch(PDOException $e){        die("Error: ".$e->getMessage());    }    return $sql;}// получаем количество задач для пагинацииfunction getTasksCount(){        try {            $q = "SELECT COUNT(*) FROM tasks";            $sql = SQL::getInstance()->Select($q);        } catch (PDOException $e) {            die("Error: " . $e->getMessage());        }        return $sql['0']['0'];}// добавляем задачуfunction setTask($id_task = 0, $task_description, $task_email){    $response = '';    $id_task = (int)$id_task;    $id_status = 2;    try {        $id_user = $_POST['name'];        $descript