<?php/** * Шаблон главной страницы */?><!-- Сортировка по статусу --><form method="post" action = 'index.php'>    Выберете статус задачи    <select name = "status">        <option value = "0">все</option>        <?php        foreach($statusData as $item) {            echo '<option value="' . $item['id'] . '">' . $item['name'] . '</option>';        }        ?>    </select>    <p><input type="submit" value="Выбрать"/></p></form><!-- Сортировка по имени пользователя --><form method="post" action = 'index.php'>    Выберете пользователя    <select name="name">        <option value = "0">все</option>        <?php        foreach($usersData as $name) {            echo '<option value="' . $name['id'] . '">' . $name['name'] . '</option>';        }    ?>    </select>    <p><input type="submit" value="Выбрать"/></p></form><!-- Сортировка по email --><form method="post" action = 'index.php'>    Выберете email    <select name="email">        <option value = "0">все</option>        <?php        foreach($usersData as $email) {            echo '<option value="' . $email['id'] . '">' . $email['email'] . '</option>';        }        ?>    </select>    <p><input type="submit" value="Выбрать"/></p></form><!-- Выводим задачи --><h3>Список задач:</h3><?foreach($tasks as $task){        echo 'Имя пользователя: ' . $task['name'] ?><br/><?        ;        echo 'Email: ' . $task['email'] ?><br/><?        ;        echo 'Задача:' . $task['description'] ?><br/><?        ;        // Ставим пометку, если задача выполнена        if ($task['id_status'] == 1){            ?>            <h4>Выполнена</h4>            <?        }        // checkbox для галочки о выполненнии    if ($task['id_status'] != 1 && isset($_SESSION['user'])    && $_SESSION['user']['id'] == 1){        ?>        <form method="post">            <p><input type="checkbox" name="completed" />Выполнена            <p><input type="hidden" name="id_task" value="<?=$task['id']?>"></p>            <input type="submit" value="Отправить" /></p>        </form>        <?}        // редактирование текста задачи для администратора    if ($_SESSION['user']['id'] == 1){        ?>        <p><a href="?act=edit&action=update&id_task=<?=$task['id']?>">Редактировать</a></p>        <?}    ?>    <hr/>    <?;}// пагинация и get-параметры для сортировкиfor ($page=1;$page<=$count_pages;$page++){    echo '<a href="index.php?page=' . $page .        '&first_result=' . ($page-1)*$results_per_page .        '&result_per_page=' . $results_per_page .        '&statusId=' . $_SESSION['statusId'] .        '&nameId=' . $_SESSION['nameId'] .        '&emailId=' . $_SESSION['emailId'] .        '">' . $page . '</a> ';}?>