<?php/** * Шаблон главной страницы */?><div class="hallo"><?=$greetings?><br/><?=nl2br($text)?></div><div class="container">    <!-- navs -->    <h2 id="page-title" class="display-4 py-3 my-3 text-center">Сортировка задач</h2>    <nav id="sort-nav" class="nav justify-content-center nav-pills flex-column flex-sm-row">        <a class="nav-link" href="?sort=<?=$sort_user_name?>">Сортировка по имени пользователя <?=$user_name?> </a>        <a class="nav-link" href="?sort=<?=$sort_email?>">Сортировка по email <?=$email?></a>        <a class="nav-link" href="?sort=<?=$sort_status?>">Сортировка по статусу <?=$status?></a>    </nav><!-- Выводим задачи --><h3>Список задач:</h3>    <p><?=$response?></p>    <!-- cards -->    <div class="row" style="background: lightblue">            <?        foreach($tasks as $task){            ?>            <div class="col-md-6 col-lg-4">                <a href="?act=one&id_task=<?=$task['task_id']?>">                <div class="card">                    <div class="card-body">                        <?                        echo 'Имя пользователя: ' . $task['user_name'] ?><br/><?                        ;                        echo 'Задача: ' . $task['task_name'] ?><br/><?                        ;                        echo 'Описание: ' . $task['description'] ?><br/><?                        ;                        echo 'Создана: ' . $task['created_at'] ?><br/><?                        ;                        echo 'Срок исполнения: ' . $task['dead_line'] ?><br/><?                        ;                        // Ставим пометку, если задача выполнена                        if ($task['id_status'] == 1){                            ?>                            <h4>Выполнена</h4>                            <?                        }?>                    </div>                </div>                </a>            </div>            <?;        }?>    </div></div><p>стр. <?=$page?></p><?// пагинация и get-параметры для сортировкиfor ($page=1;$page<=$count_pages;$page++){    echo '<a href="index.php?page=' . $page .        '&first_result=' . ($page-1)*$results_per_page .        '&result_per_page=' . $results_per_page .        '&sort=' . $_GET['sort'] .        '&order='. $order .        '">' . $page . '</a> ';}?>