<?php/** * Шаблон главной страницы */?><div class="container">    <!-- navs -->    <h2 id="page-title" class="display-4 py-3 my-3 text-center">Сортировка задач</h2>    <nav id="sort-nav" class="nav justify-content-center nav-pills flex-column flex-sm-row">        <a class="nav-link" href="?sort=<?=$sort_user_name?>">Сортировка по имени пользователя <?=$user_name?> </a>        <a class="nav-link" href="?sort=<?=$sort_email?>">Сортировка по email <?=$email?></a>        <a class="nav-link" href="?sort=<?=$sort_status?>">Сортировка по статусу <?=$status?></a>    </nav><!-- Выводим задачи --><h3>Список задач:</h3>    <!-- cards -->    <div class="row" style="background: lightblue">            <?        foreach($tasks as $task){            ?>            <div class="col-md-6 col-lg-4">                <div class="card">                    <div class="card-body">                        <?                        echo 'Имя пользователя: ' . $task['name'] ?><br/><?                        ;                        echo 'Email: ' . $task['email'] ?><br/><?                        ;                        echo 'Задача:' . $task['description'] ?><br/><?                        ;                        // Ставим пометку, если задача выполнена                        if ($task['id_status'] == 1){                            ?>                            <h4>Выполнена</h4>                            <?                        }                        // checkbox для галочки о выполненнии                        if ($task['id_status'] != 1 && isset($_SESSION['user'])                            && $_SESSION['user']['id'] == 1){                            ?>                            <form method="post">                                <p><input type="checkbox" name="completed" />Выполнена                                <p><input type="hidden" name="id_task" value="<?=$task['id']?>"></p>                                <input type="submit" value="Отправить" /></p>                            </form>                        <?}                        // редактирование текста задачи для администратора                        if ($_SESSION['user']['id'] == 1){                            ?>                            <p><a href="?act=edit&action=update&id_task=<?=$task['id']?>">Редактировать</a></p>                        <?}                        ?>                    </div>                </div>            </div>            <?;        }?>    </div></div><p>стр. <?=$page?></p><?// пагинация и get-параметры для сортировкиfor ($page=1;$page<=$count_pages;$page++){    echo '<a href="index.php?page=' . $page .        '&first_result=' . ($page-1)*$results_per_page .        '&result_per_page=' . $results_per_page .        '&sort=' . $_GET['sort'] .        '&order='. $order .        '">' . $page . '</a> ';}?>