<?php/** * Шаблон главной страницы */?><div class="container">    <div class="navigation">        <!-- navs -->        <h2 id="page-title" class="display-4 py-3 my-3 text-center"><strong>СПИСОК ЗАДАЧ:</strong></h2>        <nav id="sort-nav" class="nav justify-content-center nav-pills flex-column flex-sm-row">            <li><a class="nav-link" href="?sort=<?=$sort_user_name?>">Сортировка по имени пользователя <?=$user_name?> </a></li>            <li><a class="nav-link" href="?sort=<?=$sort_email?>">Сортировка по email <?=$email?></a></li>            <li><a class="nav-link" href="?sort=<?=$sort_status?>">Сортировка по статусу <?=$status?></a></li>        </nav>    </div>    <!-- Выводим задачи -->    <!-- cards -->    <div id="row-tasks" class="row">        <?        foreach($tasks as $task){            ?>            <div id="task_<?=$task['task_id']?>" class="col-md-6 col-lg-4 task-column"                 data-toggle="modal" data-target="#taskModal">                <a class="task-href" id="task_<?=$task['task_id']?>" href="?act=one&id_task=<?=$task['task_id']?>">                    <div class="card" id="task_<?=$task['task_id']?>">                        <div class="card-body" id="task_<?=$task['task_id']?>">                            <h3 id="task_<?=$task['task_id']?>" class="card-title"><?=$task['task_name']?></h3>                            <?                            echo $task['description'] ?><br/><br/><?                            ;                            echo 'Ответственный: ' . $task['user_name'] ?><br/><?                            ;                            echo 'Срок исполнения: ' . $task['dead_line'] ?><br/><?                            ;                            // Ставим пометку, если задача выполнена                            if ($task['id_status'] == 1){                                ?>                                <img id="task_<?=$task['task_id']?>" src="img/completed.png" width="20" alt="completed">                                <?                            }else{?>                                <img id="task_<?=$task['task_id']?>" src="img/uncompleted.png" width="20" alt="uncompleted">                            <?}                            ?>                        </div>                    </div>                </a>            </div>            <?;        }?>    </div>    <p>стр. <?=$page?></p>    <div class="pagination">        <?        // пагинация и get-параметры для сортировки        for ($page=1;$page<=$count_pages;$page++){            echo '<a href="index.php?page=' . $page .                '&first_result=' . ($page-1)*$results_per_page .                '&result_per_page=' . $results_per_page .                '&sort=' . $_GET['sort'] .                '&order='. $order .                '" class="btn">' . $page . '</a> ';        }        ?>    </div></div><?=$modal?>