<?php/** * Шаблон главной страницы */?><div class="container">    <div class="navigation">        <!-- navs -->        <h2 id="page-title" class="display-4 py-3 my-3 text-center"><strong>СПИСОК ЗАДАЧ:</strong></h2>        <nav id="sort-nav" class="nav justify-content-center nav-pills flex-column flex-sm-row">            <li><a id="sort_user_name" class="nav-link" href="?sort=<?=$sort_user_name?>"><?=$nav_title_sort_user_name?></a></li>            <li><a class="nav-link" href="?sort=<?=$sort_date?>"><?=$nav_title_sort_date?></a></li>            <li><a class="nav-link" href="?sort=<?=$sort_status?>"><?=$nav_title_sort_status?></a></li>        </nav>    </div>    <!-- Выводим задачи -->    <!-- cards -->    <div id="row-tasks" class="row">        <?        foreach($tasks as $task){            ?>            <div id="column_task_<?=$task['task_id']?>" class="col-md-6 col-lg-4 task-column task-cards"                data-toggle="modal" data-target="#taskModal"                data-sortUser = "<?=$task['user_name']?>">                <a class="task-href" id="href_task_<?=$task['task_id']?>" href="?act=one&id_task=<?=$task['task_id']?>">                    <div class="card" id="card_task_<?=$task['task_id']?>">                        <div class="card-body" id="body_task_<?=$task['task_id']?>">                            <h3 id="title_task_<?=$task['task_id']?>" class="card-title"><?=$task['task_name']?></h3>                            <p id="description_task_<?=$task['task_id']?>"><?=$task['description']?></p>                            <p id="user_task_<?=$task['task_id']?>">Ответственный:                                <span id="username_task_<?=$task['task_id']?>"> <?=$task['user_name']?></span></p>                            <p id="deadline_task_<?=$task['task_id']?>">Срок выполнения:                                <span id="deadline-span_task_<?=$task['task_id']?>"> <?=date('Y-m-d H:i', $task['dead_line'])?></span></p>                            <!-- Ставим пометку, если задача выполнена -->                            <img id="status_task_<?=$task['task_id']?>" src="                            <? if ($task['id_status'] == 1) {                                echo "img/completed.png";                            }else{                                echo "img/uncompleted.png";                            }                            ?>                            " width="20" alt="completed">                        </div>                    </div>                </a>            </div>            <?;        }?>    </div>    <p>стр. <?=$page?></p>    <div class="pagination">        <?        // пагинация и get-параметры для сортировки        for ($page=1;$page<=$count_pages;$page++){            echo '<a href="index.php?page=' . $page .                '&first_result=' . ($page-1)*$results_per_page .                '&result_per_page=' . $results_per_page .                '&sort=' . $_GET['sort'] .                '&order='. $order .                '" class="btn">' . $page . '</a> ';        }        ?>    </div></div><?=$modal?>