<?php/** * Шаблон главной страницы */?><p> <a href="?sort=user_name">Сортировка по имени пользователя <?=$user_name?> </a> |    <a href="?sort=email">Сортировка по email <?=$email?> </a> |    <a href="?sort=status">Сортировка по статусу <?=$status?> </a> |</p><!-- Выводим задачи --><h3>Список задач:</h3><?foreach($tasks as $task){        echo 'Имя пользователя: ' . $task['name'] ?><br/><?        ;        echo 'Email: ' . $task['email'] ?><br/><?        ;        echo 'Задача:' . $task['description'] ?><br/><?        ;        // Ставим пометку, если задача выполнена        if ($task['id_status'] == 1){            ?>            <h4>Выполнена</h4>            <?        }        // checkbox для галочки о выполненнии    if ($task['id_status'] != 1 && isset($_SESSION['user'])    && $_SESSION['user']['id'] == 1){        ?>        <form method="post">            <p><input type="checkbox" name="completed" />Выполнена            <p><input type="hidden" name="id_task" value="<?=$task['id']?>"></p>            <input type="submit" value="Отправить" /></p>        </form>        <?}        // редактирование текста задачи для администратора    if ($_SESSION['user']['id'] == 1){        ?>        <p><a href="?act=edit&action=update&id_task=<?=$task['id']?>">Редактировать</a></p>        <?}    ?>    <hr/>    <?;}// пагинация и get-параметры для сортировкиfor ($page=1;$page<=$count_pages;$page++){    echo '<a href="index.php?page=' . $page .        '&first_result=' . ($page-1)*$results_per_page .        '&result_per_page=' . $results_per_page .        '&order='. $order .        '">' . $page . '</a> ';}?>