<?php/** * Шаблон главной страницы * ======================= * $text - текст */?><?=$response?><!-- <h3>Редактирование</h3> --><p>Ответсвенный: <?=$user_name?></p><p>Задача: <?=$task_name?></p><?if (isset($_GET['edit']) && $_GET['edit'] == 'on'){    ?>     <form method="post" action='?act=one'/>    <p><input type="text" name="description" value="<?=$task_description?>"></p>    <p><input type="hidden" name="id_task" value="<?=$task_id?>"></p>    <p><input type="submit" value="Отправить"/></p>    </form><?}else{?>    <p>Описание: <?=$task_description?></p><?}?><p>Статус: <?=$status_name?></p><?// checkbox для галочки о выполненнииif ($status_name != 'выполнена'){    if ($_SESSION['user']['login'] == 'admin' || $_SESSION['user']['login'] == "$user_login"){    ?>    <form method="post">        <p><input type="checkbox" name="completed"/>Выполнена        <p><input type="hidden" name="id_task" value="<?=$task_id?>"></p>        <input type="submit" value="Отправить"/></p>    </form><?}}?><?// редактирование текста задачи для администратораif ($_SESSION['user']['login'] == 'admin' || $_SESSION['user']['login'] == "$user_login"){    ?>    <p><a href="?act=one&edit=on&id_task=<?=$task_id?>">Редактировать</a></p><?}?>