<?php/** * Шаблон главной страницы * ======================= * $text - текст */?><div class="task-item-container">    <?=$response?>    <h1 class="card-title"><?=$task_name?></h1>    <p>Описание: </p>    <p class="task_description"><?=$task_description?></p>    <p>Ответсвенный: <?=$user_name?></p>    <p>Создана: <?=$created_at?></p>    <p>Срок исполнения: <?=$dead_line?></p>    <p>Статус: <?=$status_name?></p>    <div class="admin-buttons">        <?        // кнопка удаления задачи            if ($_SESSION['user']['login'] == 'admin' || $_SESSION['user']['login'] == "$user_login"){            ?>            <a class="btn btn-danger" href="?act=one&delete=on&id_task=<?=$task_id?>" role="button">Удалить</a>        <?}        //если задача редактируется, убираем кнопку "редактировать"        if (!isset($_GET['edit'])) {            // редактирование текста задачи            if ($_SESSION['user']['login'] == 'admin' || $_SESSION['user']['login'] == "$user_login") {                ?>                <a class="btn btn-primary" href="index.php?c=page&act=create&id_task=<?= $task_id ?>&edit=on"                   role="button">Редактировать</a>                <?            }        }        // кнопка о выполнении задачи        if ($_SESSION['user']['login'] == 'admin' && $status_name == 'невыполнена' || $_SESSION['user']['login'] == "$user_login"            && $status_name == 'невыполнена'){            ?>            <a class="btn btn-success" href="?act=one&completed=on&id_task=<?=$task_id?>" role="button">Выполнена</a>        <?}        ?>    </div></div>